import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Import logo

const Layout = ({ children }) => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    contactNumber: '',
    email: '',
    directorEmail1: '',
    directorContact1: '',
    directorEmail2: '',
    directorContact2: '',
  });

  const [errors, setErrors] = useState({});
  const [showError, setShowError] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (!formData.name) {
      formErrors.name = 'Name is required';
      isValid = false;
    }
    if (!formData.address) {
      formErrors.address = 'Address is required';
      isValid = false;
    }
    if (!formData.contactNumber) {
      formErrors.contactNumber = 'Contact Number is required';
      isValid = false;
    }
    if (!formData.email) {
      formErrors.email = 'Email is required';
      isValid = false;
    }
    if (!formData.directorEmail1 && !formData.directorContact1) {
      formErrors.directorEmail1 = 'Director 01\'s Email/Contact Number is required';
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Submit the form if it's valid
      alert('Main user added successfully!');
    } else {
      // Show error popup if validation fails
      setShowError(true);
    }
  };

  return (
    <div
      className="font-sans min-h-screen flex flex-col"
      style={{
        backgroundColor: '#FFFFFF', // White background for the page
        margin: 0, // Remove any margin for the body
        padding: 0, // Remove any padding
      }}
    >
      {/* Header */}
      <header
        className="flex items-center justify-center p-2 mx-auto"
        style={{
          backgroundColor: '#191919', // Black header background
          color: '#FFFFFF', // White font color
          borderRadius: '30px', // Rounded header corners
          height: '50px', // Smaller header height
          width: '85%', // Header width
          marginTop: '20px', // Space from top
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
          paddingTop: '5px', // Adjust padding for compact header
          paddingBottom: '5px', // Adjust padding for compact header
        }}
      >
        {/* Logo */}
        <img
          src={logo}
          alt="Logo"
          className="mr-4"
          style={{
            width: '40px', // Logo size
            height: '40px',
            objectFit: 'contain',
          }}
        />
        {/* Title */}
        <h1
          className="text-lg font-bold flex-1 text-center"
          style={{
            color: '#FFFFFF', // White text color
          }}
        >
          Freight Allocation
        </h1>
      </header>

      {/* Navigation Bar with Added Space */}
      <div
        className="mt-4 w-85%" // Added margin-top for space between header and navbar
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <ul className="flex space-x-4 justify-center items-center">
          <li>
            <button
              className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
              style={{
                transition: 'all 0.3s ease',
              }}
              onClick={() => {}}
            >
              Home
            </button>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <Link to="/add-freight-agent">
              <button
                className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
                style={{
                  transition: 'all 0.3s ease',
                }}
              >
                Add Freight Agent
              </button>
            </Link>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <Link to="/add-freight-coordinator">
              <button
                className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
                style={{
                  transition: 'all 0.3s ease',
                }}
              >
                Add Freight Coordinator
              </button>
            </Link>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <Link to="/add-main-user">
              <button
                className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
                style={{
                  transition: 'all 0.3s ease',
                }}
              >
                Add Main User
              </button>
            </Link>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <main className="flex-grow">
        <div
          className="p-6 rounded-lg shadow-lg"
          style={{
            background: '#FFFFFF', // Solid white background
            borderRadius: '16px', // Rounded corners
            border: '1px solid rgba(25, 25, 25, 0.1)', // Subtle border
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
            width: '700px', // Custom width for the box
          }}
        >
          <h2
            className="text-2xl mb-4 font-semibold"
            style={{
              color: '#191919', // Black text for the title
              textAlign: 'center',
            }}
          >
            Add Main User
          </h2>
          {showError && (
            <div
              className="bg-red-500 text-white p-2 rounded-md mb-4"
              style={{
                textAlign: 'center',
              }}
            >
              <strong>Error: Please fill in the required fields.</strong>
            </div>
          )}
          <form className="flex flex-col" onSubmit={handleSubmit}>
            <div className="mb-3">
              <label
                htmlFor="name"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Name:
              </label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.name}
                onChange={handleInputChange}
              />
              {errors.name && <p className="text-red-500 text-xs">{errors.name}</p>}
            </div>

            <div className="mb-3">
              <label
                htmlFor="address"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Address:
              </label>
              <input
                type="text"
                id="address"
                name="address"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.address}
                onChange={handleInputChange}
              />
              {errors.address && <p className="text-red-500 text-xs">{errors.address}</p>}
            </div>

            <div className="mb-3">
              <label
                htmlFor="contactNumber"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Contact Number:
              </label>
              <input
                type="text"
                id="contactNumber"
                name="contactNumber"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.contactNumber}
                onChange={handleInputChange}
              />
              {errors.contactNumber && (
                <p className="text-red-500 text-xs">{errors.contactNumber}</p>
              )}
            </div>

            <div className="mb-3">
              <label
                htmlFor="email"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Email:
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.email}
                onChange={handleInputChange}
              />
              {errors.email && <p className="text-red-500 text-xs">{errors.email}</p>}
            </div>

            <div className="mb-3">
              <label
                htmlFor="directorEmail1"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Director 01's Email/Contact Number:
              </label>
              <input
                type="text"
                id="directorEmail1"
                name="directorEmail1"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.directorEmail1}
                onChange={handleInputChange}
              />
              {errors.directorEmail1 && (
                <p className="text-red-500 text-xs">{errors.directorEmail1}</p>
              )}
            </div>

            <div className="mb-3">
              <label
                htmlFor="directorContact1"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Director 01's Contact Number:
              </label>
              <input
                type="text"
                id="directorContact1"
                name="directorContact1"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.directorContact1}
                onChange={handleInputChange}
              />
            </div>

            <div className="mb-3">
              <label
                htmlFor="directorEmail2"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Director 02's Email/Contact Number (Optional):
              </label>
              <input
                type="text"
                id="directorEmail2"
                name="directorEmail2"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.directorEmail2}
                onChange={handleInputChange}
              />
            </div>

            <button
              type="submit"
              className="py-2 px-4 bg-orange-500 text-white rounded-md hover:bg-orange-600"
              style={{
                transition: 'all 0.3s ease',
                cursor: 'pointer',
              }}
            >
              Submit
            </button>
          </form>
        </div>
      </main>

      {/* Footer */}
      <footer
        className="p-4 text-center mt-auto"
        style={{
          backgroundColor: 'transparent', // Transparent background
          color: '#191919', // Black text
          fontSize: '14px',
          marginTop: '20px',
        }}
      >
        © {new Date().getFullYear()} Freight Allocation. All Rights Reserved.
      </footer>
    </div>
  );
};

export default Layout;
