import React, { useState, useEffect } from 'react';
import logo from '../assests/basilurlogo.png'; // Import the logo

const AddFreightCoordinator = () => {
  const [freightAgents, setFreightAgents] = useState([]); // State for available freight agents
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    contactNumber: '',
    password: '',
    selectedAgent: '',
  });

  useEffect(() => {
    // Simulate fetching data from the "Add Freight Agent" page (mocking a database call)
    setFreightAgents([
      { id: '1', name: 'Agent 1' },
      { id: '2', name: 'Agent 2' },
      { id: '3', name: 'Agent 3' },
    ]);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate sending an email notification after submitting the form
    alert(`Password for Freight Coordinator has been sent to ${formData.email}`);
  };

  return (
    <div
      className="font-sans min-h-screen flex flex-col justify-between"
      style={{
        backgroundColor: '#FFFFFF', // White background for the body
      }}
    >
      {/* Header */}
      <header
        className="flex items-center justify-center p-2 mx-auto"
        style={{
          backgroundColor: '#191919', // Black header background
          color: '#FFFFFF', // White font color
          borderRadius: '30px', // Rounded corners
          height: '50px', // Smaller header height
          width: '85%', // Narrower width
          marginTop: '20px', // Space from top
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
        }}
      >
        {/* Logo inside the header */}
        <img
          src={logo}
          alt="Logo"
          className="mr-4"
          style={{
            width: '40px', // Reduced logo size
            height: '40px',
            objectFit: 'contain',
          }}
        />

        {/* Title centered in the header */}
        <h1
          className="text-lg font-bold flex-1 text-center"
          style={{
            color: '#FFFFFF', // White text color
          }}
        >
          Freight Allocation
        </h1>
      </header>

      {/* Navigation Buttons */}
      <nav className="mt-4 mb-8 w-85%">
        <ul className="flex space-x-4 justify-center items-center">
          <li>
            <button
              className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
              style={{
                transition: 'all 0.3s ease',
              }}
              onClick={() => {}}
            >
              Home
            </button>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <button
              className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
              style={{
                transition: 'all 0.3s ease',
              }}
              onClick={() => {}}
            >
              Add Freight Agent
            </button>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <button
              className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
              style={{
                transition: 'all 0.3s ease',
              }}
              onClick={() => {}}
            >
              Add Freight Coordinator
            </button>
          </li>
          <span className="text-orange-500">|</span>
          <li>
            <button
              className="text-lg font-medium text-black hover:text-orange-500 hover:underline"
              style={{
                transition: 'all 0.3s ease',
              }}
              onClick={() => {}}
            >
              Add Main User
            </button>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main className="flex-1 flex justify-center items-center mt-8">
        <div
          className="p-6 rounded-lg shadow-lg"
          style={{
            background: '#FFFFFF', // Solid white background
            borderRadius: '16px', // Rounded corners
            border: '1px solid rgba(25, 25, 25, 0.1)', // Subtle border
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
            width: '700px', // Custom width for the box
          }}
        >
          <h2
            className="text-2xl mb-4 font-semibold"
            style={{
              color: '#191919', // Black text for the title
              textAlign: 'center',
            }}
          >
            Add Freight Coordinator
          </h2>
          <form className="flex flex-col" onSubmit={handleSubmit}>
            <div className="mb-3">
              <label
                htmlFor="name"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Name:
              </label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.name}
                onChange={handleInputChange}
              />
            </div>

            <div className="mb-3">
              <label
                htmlFor="email"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Email:
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.email}
                onChange={handleInputChange}
              />
            </div>

            <div className="mb-3">
              <label
                htmlFor="contactNumber"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Contact Number:
              </label>
              <input
                type="text"
                id="contactNumber"
                name="contactNumber"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.contactNumber}
                onChange={handleInputChange}
              />
            </div>

            <div className="mb-3">
              <label
                htmlFor="selectedAgent"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Freight Agent:
              </label>
              <select
                id="selectedAgent"
                name="selectedAgent"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.selectedAgent}
                onChange={handleInputChange}
              >
                <option value="">Select a Freight Agent</option>
                {freightAgents.map((agent) => (
                  <option key={agent.id} value={agent.id}>
                    {agent.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-3">
              <label
                htmlFor="password"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Password:
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.password}
                onChange={handleInputChange}
              />
            </div>

            <button
              type="submit"
              className="p-3 rounded-md text-lg cursor-pointer border-2 w-full mt-4"
              style={{
                borderColor: '#FF4D00',
                backgroundColor: '#FF4D00',
                color: '#FFFFFF',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
            >
              Add Freight Coordinator
            </button>
          </form>
        </div>
      </main>

      {/* Footer */}
      <footer
        className="p-4 text-center"
        style={{
          backgroundColor: 'transparent',
          color: '#191919',
          fontSize: '14px',
          marginTop: '20px',
        }}
      >
        Â© {new Date().getFullYear()} Freight Allocation. All Rights Reserved.
      </footer>
    </div>
  );
};

export default AddFreightCoordinator;
