import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png';

const AdminPage = () => {
  return (
    <div className="min-h-screen bg-lightOliveGreen">
      {/* Header stays at the top */}
      <header
        className="flex justify-between items-center p-2 mt-4 mx-auto"
        style={{
          backgroundColor: '#ffffff', // White background for the header
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Soft shadow for depth
          color: '#381e10', // Dark color for text
          borderRadius: '50px', // Rounded corners for all corners
          height: '60px', // Reduced height for a thinner header
          width: '80%', // Set width to 80% of the parent container
        }}
      >
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-8" /> {/* Smaller logo */}
        </div>
        <div className="flex-1 text-center">
          <h1
            className="text-2xl font-bold" // Smaller text size
            style={{
              color: '#381e10', // Dark color for text
            }}
          >
            Freight Allocation
          </h1>
        </div>
      </header>
      
      {/* Content is centered */}
      <div className="flex flex-col items-center justify-center min-h-screen p-6">
        <h1 className="text-2xl font-bold text-darkGreen mb-6">Admin Dashboard</h1>
        
        {/* Use flex column with gap to create space between buttons */}
        <div className="flex flex-col gap-6">
          <Link to="/add-freight-agent">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Freight Agent
            </button>
          </Link>
          
          <Link to="/add-freight-coordinator">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Freight Coordinator
            </button>
          </Link>
          
          <Link to="/add-main-user">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Main User
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
