import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Ensure the logo path is correct

const LoadingPage = () => {
  const [progress, setProgress] = useState(0);
  const [displayedText, setDisplayedText] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const text = 'Basilur Tea Exports (Pvt) Ltd.';
    let textIndex = 0;

    // Animate loading progress and text simultaneously
    const interval = setInterval(() => {
      setProgress((oldProgress) => {
        if (oldProgress < 100) {
          return oldProgress + 1;
        } else {
          clearInterval(interval);
          navigate('/login'); // Navigate after loading
          return 100;
        }
      });

      // Add letters to the text gradually after the logo is displayed
      if (progress >= 50 && textIndex < text.length) {
        setDisplayedText((prev) => prev + text[textIndex]);
        textIndex++;
      }
    }, 30);

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [navigate, progress]);

  return (
    <div className="relative flex items-center justify-center min-h-screen bg-white">
      {/* Moving and scaling logo with text */}
      <div
        className="absolute flex flex-col items-center"
        style={{
          transform: `translateY(${progress < 100 ? `-${50 - progress / 2}%` : '0%'}) scale(${
            progress < 100 ? 2 - progress / 50 : 1
          })`,
          transition: 'transform 0.5s ease',
        }}
      >
        <img src={logo} alt="Logo" className="h-64" />
        <span
          className="mt-4 text-4xl font-bold"
          style={{
            color: progress >= 50 ? '#FF4D00' : '#191919',
            opacity: progress >= 50 ? 1 : 0,
            transition: 'opacity 0.5s ease',
          }}
        >
          {displayedText}
        </span>
      </div>

      {/* Circular loading percentage */}
      <div
        className="absolute bottom-8 left-8 flex items-center justify-center w-24 h-24 border-4 border-white rounded-full"
        style={{
          visibility: progress >= 100 ? 'hidden' : 'visible',
          borderColor: '#FF4D00',
        }}
      >
        <p className="text-white text-2xl font-bold">{progress}%</p>
      </div>
    </div>
  );
};

export default LoadingPage;
