import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../assests/basilurlogo.png';

const AdminPage = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [progress, setProgress] = useState(0);
  const [displayedText, setDisplayedText] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoading) return;

    const text = 'Basilur Tea Exports (Pvt) Ltd.';
    let textIndex = 0;

    const interval = setInterval(() => {
      setProgress((oldProgress) => {
        if (oldProgress < 100) {
          return oldProgress + 1;
        } else {
          clearInterval(interval);
          setIsLoading(false); // Stop loading once complete
          return 100;
        }
      });

      // Add letters to the text gradually
      if (textIndex < text.length) {
        setDisplayedText((prev) => prev + text[textIndex]);
        textIndex++;
      }
    }, 30);

    return () => clearInterval(interval);
  }, [isLoading]);

  if (isLoading) {
    return (
      <div className="relative flex items-center justify-center min-h-screen bg-black">
        {/* Moving and scaling logo with text */}
        <div
          className="absolute flex items-center"
          style={{
            transform: `translateY(${progress < 100 ? `-${50 - progress / 2}%` : '0%'}) scale(${
              progress < 100 ? 2 - progress / 50 : 1
            })`,
            transition: 'transform 0.5s ease',
          }}
        >
          <img src={logo} alt="Logo" className="h-64" />
          <span className="ml-4 text-white text-4xl font-bold">{displayedText}</span>
        </div>

        {/* Circular loading percentage */}
        <div
          className="absolute bottom-8 left-8 flex items-center justify-center w-24 h-24 border-4 border-white rounded-full"
          style={{ visibility: progress >= 100 ? 'hidden' : 'visible' }}
        >
          <p className="text-white text-2xl font-bold">{progress}%</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-lightOliveGreen">
      {/* Header stays at the top */}
      <header className="flex justify-between items-center w-full p-4 bg-darkGreen shadow-md">
        <img src={logo} alt="Logo" className="h-12" />
        <h1 className="text-4xl font-bold text-lightBeige text-center flex-1">Freight Allocation</h1>
        {/* Empty div to balance the layout */}
        <div className="w-12" />
      </header>

      {/* Content is centered */}
      <div className="flex flex-col items-center justify-center min-h-screen p-6">
        <h1 className="text-2xl font-bold text-darkGreen mb-6">Admin Dashboard</h1>

        {/* Use flex column with gap to create space between buttons */}
        <div className="flex flex-col gap-6">
          <Link to="/add-freight-agent">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Freight Agent
            </button>
          </Link>

          <Link to="/add-freight-coordinator">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Freight Coordinator
            </button>
          </Link>

          <Link to="/add-main-user">
            <button className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg hover:bg-darkGreen transition">
              Add Main User
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
