import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Ensure the logo path is correct

const LoadingPage = () => {
  const [displayedText, setDisplayedText] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const text = 'Basilur Tea Exports (Pvt) Ltd.';
    let textIndex = 0;

    // Animate text gradually
    const interval = setInterval(() => {
      if (textIndex < text.length) {
        setDisplayedText((prev) => prev + text[textIndex]);
        textIndex++;
      } else {
        clearInterval(interval);
      }
    }, 50); // Adjust the speed of the text animation

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  const handleGetStartedClick = () => {
    navigate('/login'); // Navigate to the login page when the button is clicked
  };

  return (
    <div className="relative flex flex-col items-center justify-center min-h-screen bg-white">
      {/* Logo */}
      <img src={logo} alt="Logo" className="h-64 mb-4" />

      {/* Display the company name */}
      <span
        className="text-4xl font-bold"
        style={{
          color: '#FF4D00', // Color for the text
          opacity: displayedText.length > 0 ? 1 : 0,
          transition: 'opacity 0.5s ease',
        }}
      >
        {displayedText}
      </span>

      {/* Get Started Button */}
      {displayedText.length === 'Basilur Tea Exports (Pvt) Ltd.'.length && (
        <button
          onClick={handleGetStartedClick}
          className="mt-6 px-6 py-2 text-white bg-ff4d00 rounded-full hover:bg-orange-600 transition"
        >
          Get Started Here
        </button>
      )}
    </div>
  );
};

export default LoadingPage;
