import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Import logo

const AdminPage = () => {
  return (
    <div
      className="min-h-screen flex flex-col"
      style={{
        backgroundColor: '#FFFFFF', // White background for the page
        margin: 0, // Remove any margin for the body to ensure layout
        padding: 0, // Remove any padding that may add extra space
      }}
    >
      {/* Header */}
      <header
        className="flex items-center p-2 mx-auto"
        style={{
          backgroundColor: '#191919', // Black header background
          color: '#FFFFFF', // White font color
          borderRadius: '30px', // Rounded header corners
          height: '50px', // Smaller height
          width: '85%', // Header width
          marginTop: '20px', // Space from top
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
          paddingTop: '5px', // Adjust padding to make header more compact
          paddingBottom: '5px', // Adjust padding to make header more compact
        }}
      >
        {/* Logo */}
        <img
          src={logo}
          alt="Logo"
          className="mr-4"
          style={{
            width: '40px', // Logo size
            height: '40px',
            objectFit: 'contain',
          }}
        />
        {/* Title */}
        <h1
          className="text-lg font-bold flex-1 text-center"
          style={{
            color: '#FFFFFF', // White text color
          }}
        >
          Admin Dashboard
        </h1>
      </header>

      {/* Buttons Section */}
      <div
        className="flex justify-evenly items-center"
        style={{
          width: '85%', // Match header width
          marginTop: '10px', // Reduce space between header and buttons
          marginBottom: '0', // Remove any margin at the bottom
        }}
      >
        <Link to="/add-freight-agent">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#FF4D00', // Orange border color
              backgroundColor: 'transparent',
              color: '#FF4D00', // Orange text
              transition: 'background-color 0.3s ease, color 0.3s ease',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#FF4D00'; // Orange background on hover
              e.target.style.color = '#FFFFFF'; // White text on hover
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent'; // Transparent background
              e.target.style.color = '#FF4D00'; // Orange text
            }}
          >
            Add Freight Agent
          </button>
        </Link>

        <Link to="/add-freight-coordinator">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#FF4D00', // Orange border color
              backgroundColor: 'transparent',
              color: '#FF4D00', // Orange text
              transition: 'background-color 0.3s ease, color 0.3s ease',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#FF4D00'; // Orange background on hover
              e.target.style.color = '#FFFFFF'; // White text on hover
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent'; // Transparent background
              e.target.style.color = '#FF4D00'; // Orange text
            }}
          >
            Add Freight Coordinator
          </button>
        </Link>

        <Link to="/add-main-user">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#FF4D00', // Orange border color
              backgroundColor: 'transparent',
              color: '#FF4D00', // Orange text
              transition: 'background-color 0.3s ease, color 0.3s ease',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#FF4D00'; // Orange background on hover
              e.target.style.color = '#FFFFFF'; // White text on hover
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent'; // Transparent background
              e.target.style.color = '#FF4D00'; // Orange text
            }}
          >
            Add Main User
          </button>
        </Link>
      </div>

      {/* Footer */}
      <footer
        className="p-4 text-center mt-auto"
        style={{
          backgroundColor: 'transparent', // Transparent background
          color: '#191919', // Black text
          fontSize: '14px',
          marginTop: 'auto', // Ensures footer stays at the bottom
        }}
      >
        © {new Date().getFullYear()} Admin Dashboard. All Rights Reserved.
      </footer>
    </div>
  );
};

export default AdminPage;
