import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Import React Router for redirection

import logo from '../assets/basilurlogo.png'; // Assuming logo is in src/assets

const LoadingPage = () => {
  const [progress, setProgress] = useState(0);
  const history = useHistory(); // For redirection

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((oldProgress) => {
        if (oldProgress === 100) {
          clearInterval(interval);
          // Redirect to login page after loading completes
          history.push('/login'); // Adjust this route as needed
        }
        return Math.min(oldProgress + 1, 100);
      });
    }, 30); // Adjust this interval to control speed of loading

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [history]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-lightOliveGreen">
      <img src={logo} alt="Logo" className="h-24 mb-4" />
      <div className="w-64 h-2 bg-gray-300 rounded-full">
        <div
          className="h-full bg-mediumGreen rounded-full"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      <p className="mt-4 text-lg text-darkGreen">{progress}%</p>
    </div>
  );
};

export default LoadingPage;
