import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Ensure the logo path is correct

const AdminPage = () => {
  return (
    <div
      className="min-h-screen flex flex-col"
      style={{
        background: 'linear-gradient(to bottom right, #dab44e, #ecdc9c, #f5eecc)',
      }}
    >
      {/* Header */}
      <header
        className="flex justify-between items-center p-2 mt-4 mx-auto"
        style={{
          backgroundColor: '#ffffff',
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
          color: '#381e10',
          borderRadius: '50px',
          height: '60px',
          width: '80%',
        }}
      >
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-8" />
        </div>
        <div className="flex-1 text-center">
          <h1
            className="text-2xl font-bold"
            style={{
              color: '#381e10',
            }}
          >
            Freight Allocation
          </h1>
        </div>
      </header>

      {/* Content */}
      <main
        className="flex-1 flex flex-col items-center justify-center p-6"
        style={{ background: 'transparent' }}
      >
        <h1 className="text-2xl font-bold text-darkGreen mb-6">Admin Dashboard</h1>

        {/* Buttons */}
        <div className="flex flex-col gap-6">
          <Link to="/add-freight-agent">
            <button
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#ab7523',
                backgroundColor: 'transparent',
                color: '#ab7523',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ab7523';
                e.target.style.color = '#ffffff';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#ab7523';
              }}
            >
              Add Freight Agent
            </button>
          </Link>

          <Link to="/add-freight-coordinator">
            <button
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#ab7523',
                backgroundColor: 'transparent',
                color: '#ab7523',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ab7523';
                e.target.style.color = '#ffffff';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#ab7523';
              }}
            >
              Add Freight Coordinator
            </button>
          </Link>

          <Link to="/add-main-user">
            <button
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#ab7523',
                backgroundColor: 'transparent',
                color: '#ab7523',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ab7523';
                e.target.style.color = '#ffffff';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#ab7523';
              }}
            >
              Add Main User
            </button>
          </Link>
        </div>
      </main>
    </div>
  );
};

export default AdminPage;
