import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Import logo

const Layout = ({ children }) => {
  const [isSideNavVisible, setSideNavVisible] = useState(false);

  return (
    <div
      className="font-sans min-h-screen flex flex-col"
      style={{
        backgroundColor: '#FFFFFF', // White background for the page
        margin: 0, // Remove any margin for the body
        padding: 0, // Remove any padding
      }}
    >
      {/* Header */}
      <header
        className="flex items-center justify-center p-2 mx-auto"
        style={{
          backgroundColor: '#191919', // Black header background
          color: '#FFFFFF', // White font color
          borderRadius: '30px', // Rounded header corners
          height: '50px', // Smaller header height
          width: '85%', // Header width
          marginTop: '20px', // Space from top
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
          paddingTop: '5px', // Adjust padding for compact header
          paddingBottom: '5px', // Adjust padding for compact header
        }}
      >
        {/* Logo */}
        <img
          src={logo}
          alt="Logo"
          className="mr-4"
          style={{
            width: '40px', // Logo size
            height: '40px',
            objectFit: 'contain',
          }}
        />
        {/* Title */}
        <h1
          className="text-lg font-bold flex-1 text-center"
          style={{
            color: '#FFFFFF', // White text color
          }}
        >
          Freight Allocation
        </h1>
      </header>

      {/* Navigation Bar */}
      <div
        className="mt-4 w-85%"
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 2%',
        }}
      >
        {/* Side Navigation Button */}
        <div className="relative">
          <button
            onClick={() => setSideNavVisible(!isSideNavVisible)}
            className="text-lg font-medium text-black bg-orange-500 hover:bg-orange-600 px-4 py-2 rounded shadow focus:outline-none"
          >
            ☰ Menu
          </button>
          {isSideNavVisible && (
            <div
              className="absolute top-full left-0 mt-2 bg-white shadow-md rounded w-48"
              style={{
                zIndex: 1000, // Ensure it appears above other elements
              }}
            >
              <ul className="flex flex-col space-y-2 p-2">
                <li>
                  <Link
                    to="/in-progress"
                    className="text-black hover:text-orange-500"
                  >
                    In Progress
                  </Link>
                </li>
                <li>
                  <Link
                    to="/completed"
                    className="text-black hover:text-orange-500"
                  >
                    Completed
                  </Link>
                </li>
                <li>
                  <Link
                    to="/add-document"
                    className="text-black hover:text-orange-500"
                  >
                    Add Document
                  </Link>
                </li>
                <li>
                  <Link
                    to="/freight-agent"
                    className="text-black hover:text-orange-500"
                  >
                    Freight Agent
                  </Link>
                </li>
              </ul>
            </div>
          )}
        </div>

        {/* Search Bar */}
        <div>
          <input
            type="text"
            placeholder="Search..."
            className="border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring focus:border-orange-500"
            style={{
              width: '200px', // Search bar width
            }}
          />
        </div>
      </div>

      {/* Main Content */}
      <main className="flex-grow">{children}</main>

      {/* Footer */}
      <footer
        className="p-4 text-center mt-auto"
        style={{
          backgroundColor: 'transparent', // Transparent background
          color: '#191919', // Black text
          fontSize: '14px',
          marginTop: '20px',
        }}
      >
        © {new Date().getFullYear()} Freight Allocation. All Rights Reserved.
      </footer>
    </div>
  );
};

export default Layout;
