import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png';

const AdminPage = () => {
  return (
    <div 
      className="min-h-screen flex flex-col" 
      style={{ 
        background: 'linear-gradient(to bottom right, #143109, #AAAE7F, #D0D6B3)', 
        color: '#143109' 
      }}
    >
      {/* Header */}
      <header className="flex justify-between items-center w-full p-6 bg-[#143109] shadow-lg">
        <img src={logo} alt="Logo" className="h-14 rounded-lg shadow-md" />
        <h1 className="text-4xl font-extrabold text-[#EFEFEF] text-center">Freight Allocation</h1>
        {/* Empty div to balance layout */}
        <div className="w-14" />
      </header>

      {/* Main Content */}
      <main className="flex flex-col items-center justify-center flex-1 text-center p-6 space-y-8">
        {/* Title */}
        <h2 className="text-3xl font-bold text-[#143109] drop-shadow-md">
          Welcome to the <span className="text-[#AAAE7F]">Admin Dashboard</span>
        </h2>

        {/* Buttons with creative styling */}
        <div className="grid grid-cols-1 gap-8 w-3/4 max-w-md">
          <Link to="/add-freight-agent">
            <button className="bg-[#D0D6B3] text-[#143109] py-4 px-8 rounded-full text-xl shadow-md hover:bg-[#AAAE7F] hover:scale-105 transition-all ease-in-out">
              Add Freight Agent
            </button>
          </Link>

          <Link to="/add-freight-coordinator">
            <button className="bg-[#D0D6B3] text-[#143109] py-4 px-8 rounded-full text-xl shadow-md hover:bg-[#AAAE7F] hover:scale-105 transition-all ease-in-out">
              Add Freight Coordinator
            </button>
          </Link>

          <Link to="/add-main-user">
            <button className="bg-[#D0D6B3] text-[#143109] py-4 px-8 rounded-full text-xl shadow-md hover:bg-[#AAAE7F] hover:scale-105 transition-all ease-in-out">
              Add Main User
            </button>
          </Link>
        </div>
      </main>

      {/* Footer */}
      <footer className="w-full p-4 bg-[#EFEFEF] text-center text-sm text-[#143109]">
        Â© 2024 Freight Allocation System. All rights reserved.
      </footer>
    </div>
  );
};

export default AdminPage;
