import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assests/basilurlogo.png'; // Ensure the logo path is correct

const LoadingPage = () => {
  const [progress, setProgress] = useState(0);
  const [letters, setLetters] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Animate the progress bar and redirect after completion
    const interval = setInterval(() => {
      setProgress((oldProgress) => {
        if (oldProgress === 100) {
          clearInterval(interval);
          navigate('/login');
        }
        return Math.min(oldProgress + 1, 100);
      });
    }, 30);

    // Animate the text "Basilur Tea Exports (Pvt) Ltd."
    const text = 'Basilur Tea Exports (Pvt) Ltd.';
    let index = 0;
    const textInterval = setInterval(() => {
      if (index < text.length) {
        setLetters((prev) => [...prev, text[index]]);
        index++;
      } else {
        clearInterval(textInterval);
      }
    }, 100); // Adjust speed of letter animation

    return () => {
      clearInterval(interval);
      clearInterval(textInterval);
    };
  }, [navigate]);

  return (
    <div className="relative flex items-center justify-center min-h-screen bg-black">
      {/* Logo animation */}
      <div
        className="absolute"
        style={{
          transform: progress < 100 ? `scale(${2 - progress / 50})` : 'scale(1)',
          transition: 'transform 0.5s ease',
        }}
      >
        <img src={logo} alt="Logo" className="h-64" />
      </div>

      {/* Animated text */}
      <div className="absolute top-1/2 text-center transform -translate-y-1/2 text-white text-4xl font-bold">
        {letters.map((letter, index) => (
          <span key={index}>{letter}</span>
        ))}
      </div>

      {/* Circular popup for loading percentage */}
      <div
        className="absolute bottom-8 left-8 flex items-center justify-center w-24 h-24 border-4 border-white rounded-full"
        style={{ visibility: progress >= 100 ? 'hidden' : 'visible' }}
      >
        <p className="text-white text-2xl font-bold">{progress}%</p>
      </div>
    </div>
  );
};

export default LoadingPage;
