import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png';

const AdminPage = () => {
  return (
    <div
      className="min-h-screen flex flex-col"
      style={{
        background: 'linear-gradient(to bottom right, #8e6b24, #b58e46, #d8bc85)', // Darker shades of the existing colors
      }}
    >
      {/* Header */}
      <header
        className="flex items-center p-2 mt-4 mx-auto"
        style={{
          backgroundColor: '#ffffff', // White background
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Shadow for depth
          color: '#381e10', // Dark color for text
          borderRadius: '50px', // Rounded corners
          height: '60px', // Thinner header
          width: '80%', // Set width
        }}
      >
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-8" />
        </div>
        <div className="flex-1 text-center">
          <h1
            className="text-2xl font-bold"
            style={{
              color: '#381e10',
            }}
          >
            Admin Dashboard
          </h1>
        </div>
      </header>

      {/* Buttons */}
      <div className="flex justify-center gap-6 mt-8">
        <Link to="/add-freight-agent">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#ab7523', // Border color
              backgroundColor: 'transparent', // Transparent by default
              color: '#ab7523', // Border color applied to text
              transition: 'background-color 0.3s ease, color 0.3s ease', // Smooth hover effects
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#ab7523'; // Hover background
              e.target.style.color = '#ffffff'; // Hover text color
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent'; // Reset background
              e.target.style.color = '#ab7523'; // Reset text color
            }}
          >
            Add Freight Agent
          </button>
        </Link>

        <Link to="/add-freight-coordinator">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#ab7523',
              backgroundColor: 'transparent',
              color: '#ab7523',
              transition: 'background-color 0.3s ease, color 0.3s ease',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#ab7523';
              e.target.style.color = '#ffffff';
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#ab7523';
            }}
          >
            Add Freight Coordinator
          </button>
        </Link>

        <Link to="/add-main-user">
          <button
            className="p-3 rounded-md border-2 text-lg"
            style={{
              borderColor: '#ab7523',
              backgroundColor: 'transparent',
              color: '#ab7523',
              transition: 'background-color 0.3s ease, color 0.3s ease',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#ab7523';
              e.target.style.color = '#ffffff';
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#ab7523';
            }}
          >
            Add Main User
          </button>
        </Link>
      </div>
    </div>
  );
};

export default AdminPage;
