import React, { useState } from 'react';

import { Link } from 'react-router-dom';

const AddFreightAgent = () => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    contactNumber: '',
    email: '',
    director1Email: '',
    director1ContactNumber: '',
    director2Email: '',
    director2ContactNumber: '',
    password: '', // Added password field
  });

  const [errors, setErrors] = useState({});
  const [showErrorPopup, setShowErrorPopup] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    // Required field validation
    if (!formData.name) {
      formErrors.name = 'Name is required';
      isValid = false;
    }
    if (!formData.address) {
      formErrors.address = 'Address is required';
      isValid = false;
    }
    if (!formData.contactNumber) {
      formErrors.contactNumber = 'Contact Number is required';
      isValid = false;
    }
    if (!formData.email) {
      formErrors.email = 'Email is required';
      isValid = false;
    }
    if (!formData.password) {
      formErrors.password = 'Password is required';
      isValid = false;
    }
    // Both director1Email and director1ContactNumber are required for Director 01
    if (!formData.director1Email || !formData.director1ContactNumber) {
      formErrors.director1 = 'Both contact number and email are required for Director 01';
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert(`Freight Agent Added Successfully!`);
      console.log('Username:', formData.email);
      console.log('Password:', formData.password);
      setShowErrorPopup(false); // Hide popup if validation passes
    } else {
      setShowErrorPopup(true); // Show the error popup
    }
  };

  return (
    <Layout>
      {/* Main Content */}
      <main className="flex justify-center items-center mt-8">
        <div
          className="p-6 rounded-lg shadow-lg"
          style={{
            background: '#FFFFFF', // Solid white background
            borderRadius: '16px', // Rounded corners
            border: '1px solid rgba(25, 25, 25, 0.1)', // Subtle border
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Subtle shadow
            width: '700px', // Custom width for the box
          }}
        >
          <h2
            className="text-2xl mb-4 font-semibold"
            style={{
              color: '#191919', // Black text for the title
              textAlign: 'center',
            }}
          >
            Add Freight Agent
          </h2>
          <form className="flex flex-col" onSubmit={handleSubmit}>
            {/* Form Fields */}
            <div className="mb-3">
              <label
                htmlFor="name"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Name:
              </label>
              <input
                type="text"
                id="name"
                name="name"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.name}
                onChange={handleInputChange}
              />
              {errors.name && (
                <p className="text-red-500 text-xs italic">{errors.name}</p>
              )}
            </div>

            <div className="mb-3">
              <label
                htmlFor="address"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Address:
              </label>
              <input
                type="text"
                id="address"
                name="address"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.address}
                onChange={handleInputChange}
              />
              {errors.address && (
                <p className="text-red-500 text-xs italic">{errors.address}</p>
              )}
            </div>

            <div className="grid grid-cols-2 gap-4 mb-3">
              <div>
                <label
                  htmlFor="contactNumber"
                  className="block mb-1 text-sm"
                  style={{ color: '#191919' }}
                >
                  Contact Number:
                </label>
                <input
                  type="text"
                  id="contactNumber"
                  name="contactNumber"
                  className="w-full p-2 border rounded-md text-sm"
                  style={{
                    borderColor: '#191919',
                    backgroundColor: '#FFFFFF',
                    color: '#191919',
                  }}
                  value={formData.contactNumber}
                  onChange={handleInputChange}
                />
                {errors.contactNumber && (
                  <p className="text-red-500 text-xs italic">{errors.contactNumber}</p>
                )}
              </div>
              <div>
                <label
                  htmlFor="email"
                  className="block mb-1 text-sm"
                  style={{ color: '#191919' }}
                >
                  Email:
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  className="w-full p-2 border rounded-md text-sm"
                  style={{
                    borderColor: '#191919',
                    backgroundColor: '#FFFFFF',
                    color: '#191919',
                  }}
                  value={formData.email}
                  onChange={handleInputChange}
                />
                {errors.email && (
                  <p className="text-red-500 text-xs italic">{errors.email}</p>
                )}
              </div>
            </div>

            {/* Password field */}
            <div className="mb-3">
              <label
                htmlFor="password"
                className="block mb-1 text-sm"
                style={{ color: '#191919' }}
              >
                Password:
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="w-full p-2 border rounded-md text-sm"
                style={{
                  borderColor: '#191919',
                  backgroundColor: '#FFFFFF',
                  color: '#191919',
                }}
                value={formData.password}
                onChange={handleInputChange}
              />
              {errors.password && (
                <p className="text-red-500 text-xs italic">{errors.password}</p>
              )}
            </div>

            <div className="mt-4">
              <h3
                className="text-lg mb-2 font-semibold"
                style={{ color: '#191919' }}
              >
                Director 01
              </h3>

              <div className="grid grid-cols-2 gap-4 mb-3">
                <div>
                  <label
                    htmlFor="director1ContactNumber"
                    className="block mb-1 text-sm"
                    style={{ color: '#191919' }}
                  >
                    Contact Number:
                  </label>
                  <input
                    type="text"
                    id="director1ContactNumber"
                    name="director1ContactNumber"
                    className="w-full p-2 border rounded-md text-sm"
                    style={{
                      borderColor: '#191919',
                      backgroundColor: '#FFFFFF',
                      color: '#191919',
                    }}
                    placeholder="Enter Director 01 Contact Number"
                    value={formData.director1ContactNumber}
                    onChange={handleInputChange}
                  />
                </div>

                <div>
                  <label
                    htmlFor="director1Email"
                    className="block mb-1 text-sm"
                    style={{ color: '#191919' }}
                  >
                    Email:
                  </label>
                  <input
                    type="email"
                    id="director1Email"
                    name="director1Email"
                    className="w-full p-2 border rounded-md text-sm"
                    style={{
                      borderColor: '#191919',
                      backgroundColor: '#FFFFFF',
                      color: '#191919',
                    }}
                    placeholder="Enter Director 01 Email"
                    value={formData.director1Email}
                    onChange={handleInputChange}
                  />
                  {errors.director1 && (
                    <p className="text-red-500 text-xs italic">{errors.director1}</p>
                  )}
                </div>
              </div>
            </div>

            {/* Director 02 (Optional) */}
            <div className="mt-4">
              <h3
                className="text-lg mb-2 font-semibold"
                style={{ color: '#191919' }}
              >
                Director 02 (Optional)
              </h3>

              <div className="grid grid-cols-2 gap-4 mb-4">
                <div>
                  <label
                    htmlFor="director2ContactNumber"
                    className="block mb-1 text-sm"
                    style={{ color: '#191919' }}
                  >
                    Contact Number:
                  </label>
                  <input
                    type="text"
                    id="director2ContactNumber"
                    name="director2ContactNumber"
                    className="w-full p-2 border rounded-md text-sm"
                    style={{
                      borderColor: '#191919',
                      backgroundColor: '#FFFFFF',
                      color: '#191919',
                    }}
                    placeholder="Enter Director 02 Contact Number"
                    value={formData.director2ContactNumber}
                    onChange={handleInputChange}
                  />
                </div>

                <div>
                  <label
                    htmlFor="director2Email"
                    className="block mb-1 text-sm"
                    style={{ color: '#191919' }}
                  >
                    Email:
                  </label>
                  <input
                    type="email"
                    id="director2Email"
                    name="director2Email"
                    className="w-full p-2 border rounded-md text-sm"
                    style={{
                      borderColor: '#191919',
                      backgroundColor: '#FFFFFF',
                      color: '#191919',
                    }}
                    placeholder="Enter Director 02 Email"
                    value={formData.director2Email}
                    onChange={handleInputChange}
                  />
                </div>
              </div>
            </div>

            <button
              type="submit"
              className="p-3 rounded-md text-lg cursor-pointer border-2 w-full mt-4"
              style={{
                borderColor: '#FF4D00',
                backgroundColor: '#FF4D00',
                color: '#FFFFFF',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
            >
              Add Freight Agent
            </button>
          </form>
        </div>
      </main>

      {/* Error Popup */}
      {showErrorPopup && (
        <div className="fixed inset-0 bg-gray-700 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-semibold text-red-500">Error</h2>
            <div className="mt-2">
              <ul>
                {Object.values(errors).map((error, index) => (
                  <li key={index} className="text-sm text-red-600">{error}</li>
                ))}
              </ul>
            </div>
            <button
              onClick={() => setShowErrorPopup(false)}
              className="mt-4 p-2 rounded-md bg-red-500 text-white"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default AddFreightAgent;
