import React from 'react';
import logo from '../assests/basilurlogo.png';

const AddMainUser = () => {
  return (
    <div className="min-h-screen bg-lightOliveGreen">
      {/* Header stays at the top */}
      <header
        className="flex justify-between items-center w-full p-4 bg-darkGreen shadow-md"
        style={{
          paddingTop: '10px', // Reduced space for a more compact header
          paddingBottom: '10px', // Reduced space for a more compact header
        }}
      >
        <img src={logo} alt="Logo" className="h-12" />
        <h1 className="text-4xl font-bold text-lightBeige text-center flex-1">Freight Allocation</h1>
        {/* Empty div to balance the layout */}
        <div className="w-12" />
      </header>

      {/* Main Content Section */}
      <div className="flex flex-col items-center justify-center min-h-screen bg-lightOliveGreen p-6">
        <h1 className="text-2xl font-bold text-darkGreen mb-4">Add Main User</h1>
        
        <form className="flex flex-col gap-4 w-full max-w-md">
          {/* Add input fields for main user details */}
          <input
            type="text"
            placeholder="Name"
            className="p-3 border border-gray-300 rounded-md w-full"
          />
          <input
            type="email"
            placeholder="Email"
            className="p-3 border border-gray-300 rounded-md w-full"
          />
          <button
            type="submit"
            className="bg-mediumGreen text-white py-3 px-6 rounded-md text-lg w-full mt-4"
          >
            Add Main User
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddMainUser;
