import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assests/basilurlogo.png';

const AdminPage = () => {
  return (
    <div 
      className="min-h-screen" 
      style={{ 
        background: 'linear-gradient(135deg, #143109, #AAAE7F, #D0D6B3)' 
      }}
    >
      {/* Header stays at the top */}
      <header className="flex justify-between items-center w-full p-4 bg-[#143109] shadow-md">
        <img src={logo} alt="Logo" className="h-12" />
        <h1 className="text-4xl font-bold text-[#EFEFEF] text-center flex-1">Freight Allocation</h1>
        {/* Empty div to balance the layout */}
        <div className="w-12" />
      </header>
      
      {/* Content is centered */}
      <div className="flex flex-col items-center justify-center min-h-screen p-6">
        <h1 className="text-2xl font-bold text-[#143109] mb-6">Admin Dashboard</h1>
        
        {/* Use flex column with gap to create space between buttons */}
        <div className="flex flex-col gap-6">
          <Link to="/add-freight-agent">
            <button className="bg-[#D0D6B3] text-[#143109] py-3 px-6 rounded-md text-lg hover:bg-[#AAAE7F] transition">
              Add Freight Agent
            </button>
          </Link>
          
          <Link to="/add-freight-coordinator">
            <button className="bg-[#D0D6B3] text-[#143109] py-3 px-6 rounded-md text-lg hover:bg-[#AAAE7F] transition">
              Add Freight Coordinator
            </button>
          </Link>
          
          <Link to="/add-main-user">
            <button className="bg-[#D0D6B3] text-[#143109] py-3 px-6 rounded-md text-lg hover:bg-[#AAAE7F] transition">
              Add Main User
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
