import React from 'react';
import { Link } from 'react-router-dom'; // Assuming React Router is used for navigation

const Layout = ({ children }) => {
  return (
    <div>
      {/* Navigation Menu */}
      <header
        style={{
          background: '#FF4D00',
          padding: '10px',
          color: '#FFF',
        }}
      >
        <nav className="flex justify-around">
          <Link
            to="/page1"
            style={{
              color: '#FFF',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            Page 1
          </Link>
          <Link
            to="/page2"
            style={{
              color: '#FFF',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            Page 2
          </Link>
          <Link
            to="/page3"
            style={{
              color: '#FFF',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            Page 3
          </Link>
          <Link
            to="/page4"
            style={{
              color: '#FFF',
              textDecoration: 'none',
              fontWeight: 'bold',
            }}
          >
            Page 4
          </Link>
        </nav>
      </header>

      {/* Page Content */}
      <main>{children}</main>
    </div>
  );
};

export default Layout;
