import React from 'react';
import logo from '../assests/basilurlogo.png';
import meshBackground from '../assests/mesh-163.png'; // Import the mesh background

const LoginPage = () => {
  return (
    <div
      className="font-sans min-h-screen flex flex-col"
      style={{
        backgroundImage: `url(${meshBackground})`, // Set mesh as background
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Header with Logo */}
      <header
        className="flex items-center justify-center p-2 mx-auto"
        style={{
          backgroundColor: '#000000', // Black background
          color: '#ffffff', // White font color
          borderRadius: '50px',
          height: '50px', // Smaller header height
          width: '80%', // Adjusted width of header
          marginTop: '50px', // Space from top
        }}
      >
        {/* Logo inside the header */}
        <div
          className="flex items-center justify-center mr-4"
          style={{
            backgroundColor: '#ffffff', // White background
            padding: '10px', // Padding around the logo
            borderRadius: '50%', // Circular background
            width: '60px', // Circle size
            height: '60px', // Circle size
          }}
        >
          <img
            src={logo}
            alt="Logo"
            style={{
              width: '40px', // Logo size
              height: '40px', // Logo size
              objectFit: 'contain', // Prevent distortion
            }}
          />
        </div>

        {/* Title centered in the header */}
        <h1
          className="text-2xl font-bold flex-1 text-center"
          style={{
            color: '#ffffff', // White text color
          }}
        >
          Freight Allocation
        </h1>
      </header>

      {/* Main Content */}
      <main className="flex-1 flex justify-center items-center">
        <div
          className="p-8 rounded-lg shadow-lg w-96"
          style={{
            background: 'rgba(255, 255, 255, 0.2)', // Semi-transparent white background
            backdropFilter: 'blur(10px)', // Glassmorphism blur effect
            WebkitBackdropFilter: 'blur(10px)', // For Safari support
            border: '1px solid rgba(255, 255, 255, 0.3)', // Soft border
            borderRadius: '16px', // Rounded corners
            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)', // Shadow for depth
          }}
        >
          <h2
            className="text-2xl mb-6 font-semibold"
            style={{
              color: '#724621',
              textShadow: '1px 1px 3px rgba(0, 0, 0, 0.2)',
            }}
          >
            Login
          </h2>
          <form className="flex flex-col">
            <div className="mb-4">
              <label
                htmlFor="username"
                className="block mb-2 text-sm"
                style={{ color: '#724621' }}
              >
                Username:
              </label>
              <input
                type="text"
                id="username"
                name="username"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                  backgroundColor: 'rgba(255, 255, 255, 0.3)',
                  color: '#381e10',
                }}
              />
            </div>
            <div className="mb-4">
              <label
                htmlFor="password"
                className="block mb-2 text-sm"
                style={{ color: '#724621' }}
              >
                Password:
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: 'rgba(255, 255, 255, 0.5)',
                  backgroundColor: 'rgba(255, 255, 255, 0.3)',
                  color: '#381e10',
                }}
              />
            </div>
            <button
              type="submit"
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#ab7523',
                backgroundColor: 'transparent',
                color: '#ab7523',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ab7523';
                e.target.style.color = '#ffffff';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#ab7523';
              }}
            >
              Login
            </button>
          </form>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
