import React from 'react';
import logo from '../assests/basilurlogo.png';
import meshBackground from '../assests/mesh-163.png';

const LoginPage = () => {
  return (
    <div
      className="font-sans min-h-screen flex flex-col"
      style={{
        backgroundImage: `url(${meshBackground})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Header */}
      <header
        className="flex justify-between items-center p-2 mt-4 mx-auto"
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.2)', // Translucent white
          backdropFilter: 'blur(10px)', // Subtle blur effect
          borderRadius: '50px',
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
          height: '60px',
          width: '80%',
          border: '1px solid rgba(255, 255, 255, 0.3)',
        }}
      >
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-8" />
        </div>
        <div className="flex-1 text-center">
          <h1
            className="text-2xl font-bold"
            style={{
              color: '#FF7043', // Warm orange-pink tone
            }}
          >
            Freight Allocation
          </h1>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 flex justify-center items-center">
        <div
          className="p-8 rounded-lg shadow-lg w-96"
          style={{
            background: 'rgba(255, 255, 255, 0.1)', // Lighter transparency
            backdropFilter: 'blur(12px)', // Glassmorphism blur effect
            WebkitBackdropFilter: 'blur(12px)',
            border: '1px solid rgba(255, 255, 255, 0.3)', // Soft white border
            borderRadius: '16px',
            boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)',
          }}
        >
          <h2
            className="text-2xl mb-6 font-semibold"
            style={{
              color: '#FF7043', // Warm orange tone
              textShadow: '1px 1px 3px rgba(0, 0, 0, 0.2)',
            }}
          >
            Login
          </h2>
          <form className="flex flex-col">
            <div className="mb-4">
              <label
                htmlFor="username"
                className="block mb-2 text-sm"
                style={{ color: '#FF8A65' }} // Lighter orange
              >
                Username:
              </label>
              <input
                type="text"
                id="username"
                name="username"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: 'rgba(255, 112, 67, 0.5)', // Orange-pink border
                  backgroundColor: 'rgba(255, 255, 255, 0.2)', // Translucent white
                  color: '#5D4037', // Dark brown for text
                }}
              />
            </div>
            <div className="mb-4">
              <label
                htmlFor="password"
                className="block mb-2 text-sm"
                style={{ color: '#FF8A65' }} // Lighter orange
              >
                Password:
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: 'rgba(255, 112, 67, 0.5)',
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  color: '#5D4037',
                }}
              />
            </div>
            <button
              type="submit"
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#FF7043',
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                color: '#FF7043',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#FF7043';
                e.target.style.color = '#ffffff';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.2)';
                e.target.style.color = '#FF7043';
              }}
            >
              Login
            </button>
          </form>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
