import React from 'react';
import logo from '../assests/basilurlogo.png';

const LoginPage = () => {
  return (
    <div
      className="font-sans min-h-screen flex flex-col"
      style={{
        background: 'linear-gradient(to bottom right, #dab44e, #ecdc9c, #f5eecc)',
      }}
    >
      <header
        className="flex justify-between items-center p-2 mt-4 mx-auto"
        style={{
          backgroundColor: '#ffffff', // White background for the header
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Soft shadow for depth
          color: '#381e10', // Dark color for text
          borderRadius: '50px', // Rounded corners for all corners
          height: '60px', // Reduced height for a thinner header
          width: '80%', // Set width to 80% of the parent container
        }}
      >
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-8" /> {/* Smaller logo */}
        </div>
        <div className="flex-1 text-center">
          <h1
            className="text-2xl font-bold" // Smaller text size
            style={{
              color: '#381e10', // Dark color for text
            }}
          >
            Freight Allocation
          </h1>
        </div>
      </header>

      <main
        className="flex-1 flex justify-center items-center"
        style={{
          background: 'transparent', // Background blends with page gradient
        }}
      >
        <div
          className="p-8 rounded-lg shadow-lg w-96"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
          }}
        >
          <h2
            className="text-2xl mb-6 font-semibold"
            style={{
              color: '#724621',
              textShadow: '1px 1px 3px rgba(0, 0, 0, 0.2)',
            }}
          >
            Login
          </h2>
          <form className="flex flex-col">
            <div className="mb-4">
              <label
                htmlFor="username"
                className="block mb-2 text-sm"
                style={{ color: '#724621' }}
              >
                Username:
              </label>
              <input
                type="text"
                id="username"
                name="username"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: '#dab44e',
                  backgroundColor: '#fbf8eb',
                  color: '#381e10',
                }}
              />
            </div>
            <div className="mb-4">
              <label
                htmlFor="password"
                className="block mb-2 text-sm"
                style={{ color: '#724621' }}
              >
                Password:
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="w-full p-3 border rounded-md text-sm"
                style={{
                  borderColor: '#dab44e',
                  backgroundColor: '#fbf8eb',
                  color: '#381e10',
                }}
              />
            </div>
            <button
              type="submit"
              className="p-3 rounded-md text-lg cursor-pointer border-2"
              style={{
                borderColor: '#ab7523', // Border color for the button
                backgroundColor: 'transparent', // Transparent background by default
                color: '#ab7523', // Border color applied to text
                transition: 'background-color 0.3s ease, color 0.3s ease', // Smooth transition for color change
              }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ab7523'; // Change background color on hover
                e.target.style.color = '#ffffff'; // Change text color on hover
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent'; // Reset background on mouse out
                e.target.style.color = '#ab7523'; // Reset text color on mouse out
              }}
            >
              Login
            </button>
          </form>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
